import { getHeroeById, getHeroeByOwner } from "./bases/08-imp-exp";

const promesa = new Promise( ( resolve, reject) => {

    setTimeout( () => {
        // Tarea
        //Importar getHeroeById 

        const heroe = ( getHeroeById( 15 ) );
        resolve( heroe );
    }, 2000);

});

// Promesa satisfactoria
promesa.then( ( heroe ) => {
    console.log('Heroe', heroe);
})
    .catch( err => {
        console.warn( err );
    })
